package regression;import org.testng.Assert;import org.testng.annotations.Test;import page_objects.abh_restaurant.*;import testUtils.TestBase;import java.text.SimpleDateFormat;import java.util.Date;public class  RegistationWithExistingMail extends TestBase {    private static final String FIRST_NAME = "Alice";    private static final String LAST_NAME = "Lopez";    private static final String EMAIL = "main@gmail.com";    private static final String PHONE_NUMBER = "12345";    private static final String ADDRESS = "Pijacna 118";    private static final String PASSWORD = "1234";    private static final String HEADER_TEXT = "Make a free reservation";    private static final String MailErrorText = "User with given email already exists.";    @Test(priority = 0)    public void openRestaurantsHomePage() {        new HomePage(driver)                .clickLoginButton(2);    }    @Test(priority = 1)    public void openRegistrationPage() {        new LoginPage(driver)                .clickRegisterButton();    }    @Test(priority = 2)    public void populateRegistrationForm() {        SimpleDateFormat formatter = new SimpleDateFormat("ddMMHHmm");        Date date = new Date();        new Registration(driver)                .makeRegistration(FIRST_NAME, LAST_NAME, EMAIL, PHONE_NUMBER, ADDRESS, PASSWORD, PASSWORD);    }    @Test(priority = 3)    public void checkForError() {        Assert.assertTrue(new Registration(driver).checkForError(MailErrorText));    }}